import React, { useState, useEffect } from "react";
import Body from "./Body";

function Home() {
	const [games, setGame] = useState([]);

	const getGameData = async () => {
		const url =
			"https://partners.9ijakids.com/index.php?partnerId=555776&accessToken=l0lawtvv-94bv-oi4d-u808-5ubz&action=catalogfilter";
		const response = await fetch(url);
		const result = await response.json();
		setGame(result);
    };

    const findMatches = (searchWord, games) => {
        return games.filter(game => {
            const regex = new RegExp(searchWord, 'gi');
            return game.title.match(regex) || game.description.match(regex)
        })

    }

    const displayMatch = (e) => {
        console.log(e.target.value)
    }

	useEffect(() => {
		getGameData();
	});

	return (
		<div>
			<h2>9ijakids Game List</h2>
			<input type="search" onChange={displayMatch}/>
			<div className="body">
				{" "}
				{games.map((game) => (
					<Body
						title={game.GameTitle}
						image={game.GameImage}
						description={game.GameDescription}
					/>
				))}
			</div>
		</div>
	);
}

export default Home;
