import React, { useState, useEffect } from "react";
import SearchIcon from "@material-ui/icons/Search";
import "./home.css";
import Body from "./Body";

function Home() {
	const [templates, setTemplate] = useState([]);
	const [topics, setTopic] = useState([]);
	const [display, setDisplay] = useState(true);
	const [list, setList] = useState(true);
	const [value, setValue] = useState("");

	const getTemplateData = async () => {
		const url =
			"https://front-end-task-dot-fpls-dev.uc.r.appspot.com/api/v1/public/task_templates";
		const response = await fetch(url);
		const result = await response.json();
		setTemplate(result);
	};

	const filter = (searchWord, templates) => {
		return templates.filter((template) => {
			const regex = new RegExp(searchWord, "gi");
			return (
				template.Group.match(regex) ||
				template.Level.match(regex) ||
				template.Topic.match(regex)
			);
		});
	};

	const displayMatch = (e) => {
		setValue(e.target.value);
		if (e.target.value === "") {
			setDisplay(false);
		} else {
            const matchSearch = filter(e.target.value, templates);
            setTopic(matchSearch);
			setDisplay(true);
			setList(true);
		}
	};
	const displayTopic = (e) => {
		const matchSearch = filter(e.target.innerText, templates);
		setTopic(matchSearch);
		setValue(e.target.innerText);
		setList(false);
    };
    const displayGroup = (e) => {
		const matchSearch = filter(e.target.value, templates);
        setTopic(matchSearch);
        setList(false)
        setDisplay(true)
	};

	useEffect(() => {
		getTemplateData();
	});

	return (
		<div className="home">
			<h2>9ijakids Game List</h2>
			<div className="home-search">
				<div className="home-list">
					<div className="home-searchBar">
						<input
							type="search"
							onChange={displayMatch}
							className="search"
							placeholder="Search Templates"
							value={value}
						/>
						<div className="home-searchBar--icon">
							<SearchIcon />
						</div>
					</div>
					{display ? (
						<ul className="topic-list">
							{topics.map((game) =>
								list ? <li onClick={displayTopic}>{game.Topic}</li> : " "
							)}
						</ul>
					) : (
						""
					)}
				</div>
				<div className="home-filter">
					<h3>Sort By:</h3>
					<select name="" onChange={displayGroup}>
						<option value="All">All</option>
						<option value="Education">Education</option>
						<option value="E-Commerce">E-Commerce</option>
						<option value="Health">Health</option>
					</select>
					<select name="" onChange={displayGroup}>
						<option value="">Default</option>
						<option value="Ascending">Ascending</option>
						<option value="Descending">Descending</option>
					</select>
				</div>
			</div>
			{display ? (
				<div className="body">
					{" "}
					{topics.map((topic) => (
						<Body
							title={topic.GameTitle}
							image={topic.GameImage}
							description={topic.GameDescription}
						/>
					))}
				</div>
			) : (
				""
			)}
		</div>
	);
}

export default Home;
