import React, { useState, useEffect } from "react";
import Body from "./Body";

function Home() {
    const [games, setGame] = useState([]);
    const [topics, setTopic] = useState([])

	const getGameData = async () => {
		const url =
			"https://partners.9ijakids.com/index.php?partnerId=555776&accessToken=l0lawtvv-94bv-oi4d-u808-5ubz&action=catalogfilter";
		const response = await fetch(url);
		const result = await response.json();
		setGame(result);
    };

    const filterTopic = (searchWord, games) => {
        return games.filter(game => {
            const regex = new RegExp(searchWord, 'gi');
            return game.Topic.match(regex)
        })

    }

    const filterGroup = (searchWord, games) => {
        return games.filter(game => {
            const regex = new RegExp(searchWord, 'gi');
            return game.Group.match(regex)
        })
    }

    const displayMatch = (e) => {
        const matchSearch = filterTopic(e.target.value, games);
        setTopic(matchSearch)
    }

    const displayGroup = (e) => {
        const matchSearch = filterTopic(e.target.value, games);
        setTopic(matchSearch)
    }

	useEffect(() => {
		getGameData();
	});

	return (
		<div>
			<h2>9ijakids Game List</h2>
			<input type="search" onChange={displayMatch}/>
            <select name="" >
                <option value="">Groups</option>
                <option value="">Academic</option>
                <option value="">Finan</option>
                <option value=""></option>

            </select>
			<div className="body">
				{" "}
				{topics.map((topic) => (
					<Body
						title={topic.GameTitle}
						image={topic.GameImage}
						description={topic.GameDescription}
					/>
				))}
			</div>
		</div>
	);
}

export default Home;
