import React, { useState, useEffect } from "react";
import SearchIcon from "@material-ui/icons/Search";
import "./home.css";
import Body from "./Body";

function Home() {
	const [templates, setTemplate] = useState([]);
	const [currentTemplate, setCurrentTemplate] = useState([]);
	const [display, setDisplay] = useState(true);
	const [list, setList] = useState(false);
	const [value, setValue] = useState("");
	const [currentPage, setCurrentPage] = useState(1);
	const [tempsPerPage, setTempsPerPage] = useState(15);
	const indexOfLastTemp = currentPage * tempsPerPage;
	const indexOfFirstTemp = indexOfLastTemp - tempsPerPage;
	const [header, setHeader] = useState("All");
	const [category, setCategory] = useState([
		"All",
		"E-commerce",
		"Eduction",
		"Health",
	]);
	const [sort, setSort] = useState(["Default", "Ascending", "Descending"]);

	const getTemplateData = async () => {
		const url =
			"https://front-end-task-dot-fpls-dev.uc.r.appspot.com/api/v1/public/task_templates";
		const response = await fetch(url);
		const result = await response.json();
		setTemplate(result.slice(1, 21));
		setCurrentTemplate(result.slice(indexOfFirstTemp, indexOfLastTemp));
	};

	const displayTemplate = (match) => {
		return setCurrentTemplate(match.slice(indexOfFirstTemp, indexOfLastTemp));
	};
	const filter = (searchWord, templates) => {
		return templates.filter((template) => {
			const regex = new RegExp(searchWord, "gi");
			const category = template.category.toString();
			return template.name.match(regex) || category.match(regex);
		});
	};

	const searchByName = (e) => {
		if (e.target.value === "") {
			setList(false);
		} else {
			displayTemplate(filter(e.target.value, templates));
			setList(true);
		}
		setValue(e.target.value);
	};
	const displayList = (e) => {
		displayTemplate(filter(e.target.innerText, templates));
		setValue(e.target.innerText);
		setList(false);
	};
	const searchByCategory = (e) => {
		if (e.target.value === "All") {
			displayTemplate(templates);
		} else {
			displayTemplate(filter(e.target.value, templates));
		}
		setList(false);
		setDisplay(true);
		setHeader(e.target.value);
	};
	const sortByName = (e) => {
		if (e.target.value === "Ascending") {
			const ascending = templates.sort((a, b) => {
				const first = a.name.toLowerCase();
				const second = b.name.toLowerCase();
				return first > second ? 1 : -1;
			});
			displayTemplate(ascending);
		} else if (e.target.value === "Descending") {
			const desc = templates.sort((a, b) => {
				const first = a.name.toLowerCase();
				const second = b.name.toLowerCase();
				return first < second ? 1 : -1;
			});
			displayTemplate(desc);
		} else if (e.target.value === "Default") {
			displayTemplate(templates);
		}
	};
	const sortByDate = (e) => {
		if (e.target.value === "Ascending") {
			const ascending = templates.sort((a, b) =>
				a.created > b.created ? 1 : -1
			);
			displayTemplate(ascending);
		} else if (e.target.value === "Descending") {
			const desc = templates.sort((a, b) => (a.created < b.created ? 1 : -1));
			displayTemplate(desc);
		} else if (e.target.value === "Default") {
			displayTemplate(templates);
		}
	};
	const handleNext = (e) => {
		const next = Number(e.target.id) + 1;
		setCurrentPage(next);
	};
	const handlePrev = (e) => {
		const prev = Number(e.target.id) - 1;
		setCurrentPage(prev);
	};
	const pageNumbers = [];
	for (let i = 1; i <= Math.ceil(templates.length / tempsPerPage); i++) {
		pageNumbers.push(i);
	}

	useEffect(() => {
		getTemplateData();
	}, []);
	useEffect(() => {
		displayTemplate(templates);
	}, [currentPage]);

	return (
		<div className="home">
			<div className="home-search">
				<div className="home-list">
					<div className="home-searchBar">
						<input
							type="search"
							onChange={searchByName}
							className="search"
							placeholder="Search Templates"
							value={value}
						/>
						<div className="home-searchBar--icon">
							<SearchIcon />
						</div>
					</div>
					{display && list ? (
						<ul className="topic-list">
							{currentTemplate.map((template) => (
								<li onClick={displayList}>{template.name}</li>
							))}
						</ul>
					) : (
						""
					)}
				</div>
				<div className="home-filter">
					<h3>Sort By:</h3>
					<div className="filters">
						<fieldset>
							<legend> Category </legend>
							<select name="" onChange={searchByCategory}>
								{category.map((category) => (
									<option value={category}>{category}</option>
								))}
							</select>
						</fieldset>
						<fieldset>
							<legend> Order </legend>
							<select name="" onChange={sortByName}>
								{}
								<option value="">Default</option>
								<option value="Ascending">Ascending</option>
								<option value="Descending">Descending</option>
							</select>
						</fieldset>
						<fieldset>
							<legend> Date </legend>
							<select name="" onChange={sortByDate}>
								<option value="">Default</option>
								<option value="Ascending">Ascending</option>
								<option value="Descending">Descending</option>
							</select>
						</fieldset>
					</div>
				</div>
			</div>
			<div className="banner">
				<img src="/../../Groupinfo.png" alt="info" />
				<p>
					Tada! Get started with a free template. Can’t find what you are
					looking for? Search from the 1000+ available templates
				</p>
			</div>
			<div className="template-heading">
				<h2>{header} Templates</h2>
				<p>{templates.length}</p>
			</div>
			<div className="body">
				{" "}
				{currentTemplate.map((template) => (
					<Body
						title={template.name}
						description={template.description}
						link={template.link}
					/>
				))}
			</div>
			<ul className="page-numbers">
				<li id={currentPage} onClick={handlePrev}>
					Prev
				</li>
				<li className="current-page">{currentPage}</li>
				<li>of {pageNumbers.length}</li>
				<li id={currentPage} onClick={handleNext}>
					Next
				</li>
			</ul>
		</div>
	);
}

export default Home;
