import React, { useState, useEffect } from "react";
import SearchIcon from "@material-ui/icons/Search";
import "./home.css";
import Body from "./Body";

function Home() {
	const [templates, setTemplate] = useState([]);
	const [topics, setTopic] = useState([]);
	const [display, setDisplay] = useState(true);
	const [list, setList] = useState(false);
	const [value, setValue] = useState("");
	const [currentPage, setCurrentPage] = useState(1);
	const [tempsPerPage, setTempsPerPage] = useState(15);
	const indexOfLastTemp = currentPage * tempsPerPage;
	const indexOfFirstTemp = indexOfLastTemp - tempsPerPage;
	const [header, setHeader] = useState("All")

	const getTemplateData = async () => {
		const url =
			"https://front-end-task-dot-fpls-dev.uc.r.appspot.com/api/v1/public/task_templates";
		const response = await fetch(url);
		const result = await response.json();
		setTemplate(result.slice(1,21));
		setTopic(templates.slice(indexOfFirstTemp, indexOfLastTemp));
	};

	const filter = (searchWord, templates) => {
		return templates.filter((template) => {
			const regex = new RegExp(searchWord, "gi");
			const category = template.category.toString();
			return template.name.match(regex) || category.match(regex);
		});
	};

	const dis = () => {
		setTopic(templates.slice(indexOfFirstTemp, indexOfLastTemp));
	}

	const displayMatch = (e) => {
		// if (e.target.value === "") {
		// 	setDisplay(false);
		// } else {
		const matchSearch = filter(e.target.value, templates);
		setValue(e.target.value);
		setTopic(matchSearch.slice(indexOfFirstTemp, indexOfLastTemp));
		// setDisplay(true);
		setList(true);
		// }
	};
	const displayList = (e) => {
		const matchSearch = filter(e.target.innerText, templates);
		setTopic(matchSearch.slice(indexOfFirstTemp, indexOfLastTemp));
		setValue(e.target.innerText);
		setList(false);
	};
	const displayCategory = (e) => {
		const matchSearch = filter(e.target.value, templates);
		setTopic(matchSearch.slice(indexOfFirstTemp, indexOfLastTemp));
		setList(false);
		setDisplay(true);
		setHeader(e.target.value);
	};
	const sortByName = (e) => {
		if (e.target.value === "Ascending") {
			const ascending = templates.sort((a, b) => {
				const first = a.name.toLowerCase();
				const second = b.name.toLowerCase();
				return first > second ? 1 : -1;
			});
			setTopic(ascending.slice(indexOfFirstTemp, indexOfLastTemp));
		} else if (e.target.value === "Descending") {
			const desc = templates.sort((a, b) => {
				const first = a.name.toLowerCase();
				const second = b.name.toLowerCase();
				return first < second ? 1 : -1;
			});
			setTopic(desc.slice(indexOfFirstTemp, indexOfLastTemp));
		}
	};
	const sortByDate = (e) => {
		if (e.target.value === "Ascending") {
			const ascending = templates.sort((a, b) => a > b ? 1 : -1);
			setTopic(ascending.slice(indexOfFirstTemp, indexOfLastTemp));
		} else if (e.target.value === "Descending") {
			const desc = templates.sort((a, b) => a < b ? 1 : -1);
			setTopic(desc.slice(indexOfFirstTemp, indexOfLastTemp));
		}
	};
	const handleNext = (e) => {
		const next = Number(e.target.id) + 1;
		setTopic(templates.slice(indexOfFirstTemp, indexOfLastTemp));
		setCurrentPage(next);
	};
	const handlePrev = (e) => {
		const prev = Number(e.target.id) - 1;
		setTopic(templates.slice(indexOfFirstTemp, indexOfLastTemp));
		setCurrentPage(prev);
	};
	const pageNumbers = [];
	for (let i = 1; i <= Math.ceil(templates.length / tempsPerPage); i++) {
		pageNumbers.push(i);
	}

	useEffect(() => {
		getTemplateData();
	},[]);

	return (
		<div className="home">
			<div className="home-search">
				<div className="home-list">
					<div className="home-searchBar">
						<input
							type="search"
							onChange={displayMatch}
							className="search"
							placeholder="Search Templates"
							value={value}
						/>
						<div className="home-searchBar--icon">
							<SearchIcon />
						</div>
					</div>
					{display && list ? (
						<ul className="topic-list">
							{topics.map((template) => (
								<li onClick={displayList}>{template.name}</li>
							))}
						</ul>
					) : (
						""
					)}
				</div>
				<div className="home-filter">
					<h3>Sort By:</h3>
					<div className="filters">
						<fieldset>
							<legend> Category </legend>
							<select name="" onChange={displayCategory}>
								<option value="All">All</option>
								<option value="Education">Education</option>
								<option value="E-Commerce">E-Commerce</option>
								<option value="Health">Health</option>
							</select>
						</fieldset>
						<fieldset>
							<legend> Order </legend>
							<select name="" onChange={sortByName}>
								<option value="">Default</option>
								<option value="Ascending">Ascending</option>
								<option value="Descending">Descending</option>
							</select>
						</fieldset>
						<fieldset>
							<legend> Date </legend>
							<select name="" onChange={sortByDate}>
								<option value="">Default</option>
								<option value="Ascending">Ascending</option>
								<option value="Descending">Descending</option>
							</select>
						</fieldset>
					</div>
				</div>
			</div>
			<div className="template-heading">
					<h2>{header} Templates</h2>
					<p>{templates.length}</p>
			</div>
			<div className="body">
				{" "}
				{topics.map((topic) => (
					<Body
						title={topic.name}
						description={topic.description}
						link={topic.link}
					/>
				))}
			</div>
			<ul className="page-numbers">
				<li id={currentPage} onClick={handlePrev}>Prev</li>
				<li>
						{currentPage}
				</li>
				<li>of {pageNumbers.length}</li>
				<li id={currentPage} onClick={handleNext}>Next</li>
			</ul>
		</div>
	);
}

export default Home;
